<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/7/31 * Time: 9:59 */namespace app\admin\controller;use app\admin\model\Access as AccessModel;use think\Request;class Access extends Base{    //列表页显示    public function index(Request $request)    {        if ($request->isAjax()) {            try {                $model = new AccessModel();                $info = $model->findall();                echo ajax($info, count($info), 'Success');            } catch (\Exception $exception) {                return $exception->getMessage();            }        }else{            return $this->fetch();        }    }    //添加    public function add_img(Request $request)    {        $model = new AccessModel();        if ($request->isPost()) {            //取所有值 先验证            $arr = json_decode($request->param('data'), true);            //字段验证            if(empty($arr['access_name'])){                return 2; //权限名称不能为空            }else if(empty($arr['access_url'])){                return 3; //权限url不能为空            }else{                $info = $model->add_img($arr);                return $info;            }        }        $info = $model->findall();        $this->assign('info',$info);        return $this->fetch();    }    //删除    public function img_del(Request $request)    {        if (!$request->param('id')) {            //参数不存在修改失败            return 2;        }else{            $model = new AccessModel();            $info = $model->img_del($request->param('id'));            return $info;        }    }    //修改    public function update_edit(Request $request)    {        if ($request->isAjax()) {            //取所有值 先验证            $arr = json_decode($request->param('data'), true);            //字段验证            if(empty($arr['access_name'])){                return 2; //标题不能为空            }else if(empty($arr['access_url'])){                return 3; //许可证类型不能为空            }else{                $model = new AccessModel;                $info = $model->updateedit($arr);                return $info;            }        }else{            $model = new AccessModel();            $info   = $model->updateinfo($request->param('id'));            $access = $model->findall();            $this->assign('info', $info);            $this->assign('access', $access);            return $this->fetch();        }    }    public function findall()    {        $accesssmodel = new AccessModel();        return $accesssmodel->findall();    }}