<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/7/31 * Time: 15:26 */namespace app\admin\controller;use app\admin\model\HeadPic as HeadPicModel;use app\admin\validate\HeadPic as HeadPicValidete;use think\Request;/* * 网站管理 */class HeadPic extends Base{    /*     * 网站设置     */    public function index(Request $request)    {        $queryymodel = new HeadPicModel();        $info = $queryymodel->findinfo();        $this->assign('info',$info);        //添加或跟新数据        if($request->isAjax()){            $date = $request->param('data');            // dump($date);            // die;            //参数验证            // $queryyValidete = new HeadPicValidete();            // if(!$queryyValidete->scene('add')->check($date)){            //     return $queryyValidete->getError();            // }            return $queryymodel->editinfo($info,$date);        }        return $this->fetch();    }}