<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/7/26 * Time: 15:12 */namespace app\admin\controller;use app\admin\model\LoveHistory as LoveHistoryModel;use app\admin\validate\LoveHistory as LoveHistoryValidate;use think\Request;class LoveHistory extends Base{    //列表页显示    public function index(Request $request)    {        if ($request->isAjax()) {            try {                $data=[];                if ($request->param('id')) {                    $data[] = ['id', '=', $request->param('id')];                }                if ($request->param('history_year')) {                    $data[] = ['history_year', '=', $request->param('history_year')];                }                $model = new LoveHistoryModel();                $limit = $request->param('limit');                $info = $model->index($data,$limit);                echo ajax($info->toArray()['data'], $info->toArray()['total'], 'Success');            } catch (\Exception $exception) {                return $exception->getMessage();            }        }else{            return $this->fetch();        }    }    //添加    public function add_img(Request $request)    {        if ($request->isPost()) {            //取所有值 先验证            $arr = json_decode($request->param('data'), true);           // dump($arr);           // die;            //验证参数            $validate = new LoveHistoryValidate();            if (!$validate->scene('add_img')->check(trimall($arr))) {                return ($validate->getError());            }            $model = new LoveHistoryModel();            $info = $model->add_img($arr);            return $info;        }        return $this->fetch();    }    //删除    public function img_del(Request $request)    {        if (!$request->param('id')) {            //参数不存在修改失败            return 2;        }else{            $model = new LoveHistoryModel();            $info = $model->img_del($request->param('id'));            return $info;        }    }    //修改    public function update_edit(Request $request)    {        if ($request->isAjax()) {            //取所有值 先验证            $arr = json_decode($request->param('data'), true);            //验证参数            $validate = new LoveHistoryValidate();            if (!$validate->scene('update_edit')->check(trimall($arr))) {                return ($validate->getError());            }            $model = new LoveHistoryModel;            $info = $model->updateedit($arr);            return $info;        }else{            //取轮播图find一条在修改前显示的数据            $model = new LoveHistoryModel();            $arr = $model->updateinfo($request->param('id'));            $this->assign('arr', $arr);            return $this->fetch();        }    }}