<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/7/26 * Time: 15:12 */namespace app\admin\model;class City extends BaseModel{    /**     * 省、市、县     * @param mixed $date     * @return array|mixed|\PDOStatement|string|\think\Collection     * @throws \think\db\exception\DataNotFoundException     * @throws \think\db\exception\ModelNotFoundException     * @throws \think\exception\DbException     */    public function find($date)    {        $info = $this            ->where('city_type',$date)            ->select();        return $info;    }    /**     * 根据父类id查找子信息     * @param $date     * @return array|\PDOStatement|string|\think\Collection     * @throws \think\db\exception\DataNotFoundException     * @throws \think\db\exception\ModelNotFoundException     * @throws \think\exception\DbException     */    public function findcity($date)    {        $info = $this            ->where('parent_id',$date)            ->select();        if($info){            $info = $info->toArray();        }        return $info;    }}