<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/8/8 * Time: 13:24 */namespace app\admin\model;class RoleAccess extends BaseModel{    /**     * 添加数据     * @param $access     * @param $id     * @return \think\Collection     * @throws \Exception     */    public function addall($access,$role_id)    {        $date = [];        foreach($access as $k=>$v){            $date[] = [                'access_id'     => $v,                'role_id'       => $role_id,                'create_time'   => date('Y-m-d H:i:s', time()),                'update_time'   => date('Y-m-d H:i:s', time()),            ];        }        return $this->saveAll($date);    }    /**     * 删除权限     * @param $where     * @return bool     */    public static function deleteAll($where)    {        return self::where($where)->delete();    }    /**     * 查找角色拥有权限     * @param $role_id     * @return array|\PDOStatement|string|\think\Collection     * @throws \think\db\exception\DataNotFoundException     * @throws \think\db\exception\ModelNotFoundException     * @throws \think\exception\DbException     */    public function findAccess($role_id)    {        return $this->where('role_id',$role_id)->column('access_id');    }}